INSERT INTO `cmssp_drug`(`id`, `tag`, `name`, `tone`, `icon`, `summary`, `usage`, `ext_data`) VALUES (null,null,'麦门冬','Mài Mén Dōnɡ',null,'<section>
    <dl>
    <dd><img class="img-responsive detail-content-img" src="http://image.dajiazhongyi.com/upload/material/image/2015/07/16/1437027628633974.jpg!lw-720"></dd>
    </dl>
