INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'一物瓜蒂汤 （金匮要略）','null','<section>
    <h4>出处</h4>
    <p>《金匮要略》卷上痓湿暍病脉证第二





</p>
</section>
<section>
    <h4>古代剂量</h4>
    <dl>
<dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="605">瓜蒂</a>（二十个，一本云二七个）</dd>
    </dl>
</section>
<section>
    <h4>现代剂量</h4>
    <dl>
        <dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="605">瓜蒂</a>（20个，一本云14个）</dd>
    </dl>
</section>
<section>
    <h4>煎服法</h4>
    <dl>
<dd>上<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1489">剉</a>，以水一升，煮取五合，去滓顿服。</dd>
    </dl>
</section>
    <section>
        <h4>方歌</h4>
        <dl>
            <dt>《金匮方歌括》陈修园</dt></p><p><dd>暍病阴阳认要真，热疼身重得其因，暑为湿恋名阴暑，二十甜瓜蒂可珍。</dd>
        </dl>
    </section>
','<section>
    <dl>
<dd>太阳中<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1494">暍</a>，身热疼重，而脉微弱，此以夏月伤冷水，水行皮中所致也。<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="1153">一物瓜蒂汤</a>主之。</dd>
    </dl>
</section>
',null);
