INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'三物备急丸（金匮要略）','null','<section>
    <h4>出处</h4>
    <p>《金匮要略》卷下杂疗方第二十三




</p>
</section>
<section>
    <h4>古代剂量</h4>
    <dl>
<dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="14483">大黄</a>（一两） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="123">干姜</a>（一两） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="241">巴豆</a>（一两，去皮心，熬，外研如脂）</dd>
    </dl>
</section>
<section>
    <h4>现代剂量</h4>
    <dl>
        <dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="14483">大黄</a>（3g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="123">干姜</a>（3g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="241">巴豆</a>（3g，去皮心，熬，外研如脂）</dd>
    </dl>
</section>
<section>
    <h4>煎服法</h4>
    <dl>
<dd>上药各须精新，先捣<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="14483">大黄</a>、<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="123">干姜</a>为末，研<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="241">巴豆</a>内中，合治一千杵，用为散，蜜和丸亦佳，密器中贮之，莫令歇。主心腹诸卒暴百病。若中恶<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="2107">客忤</a>，心腹胀满，卒痛如锥刺，气急<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="2358">口噤</a>，停尸卒死者，以暖水若酒服大豆许三四丸，或不下，捧头起，灌令下咽，须臾当差，如未差，更与三丸，当腹中鸣，即吐下便差。若<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="2358">口噤</a>，亦须折齿灌之。</dd>
    </dl>
</section>
','<section>
    <dl>
<dd>主心腹诸卒暴百病。</dd>
    </dl>
</section>
',null);
INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'三物白散（伤寒论）','null','<section>
    <h4>出处</h4>
    <p>《伤寒论》











</p>
</section>
<section>
    <h4>古代剂量</h4>
    <dl>
<dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="97">桔梗</a>（三分） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="241">巴豆</a>（一分，去皮心，熬黑，研如脂） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="19">贝母</a>（三分）</dd>
    </dl>
</section>
<section>
    <h4>现代剂量</h4>
    <dl>
        <dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="97">桔梗</a>（9g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="241">巴豆</a>（3g，去皮心，熬黑，研如脂） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="19">贝母</a>（9g）</dd>
    </dl>
</section>
<section>
    <h4>煎服法</h4>
    <dl>
<dd>上三味，为散。内<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="241">巴豆</a>更于臼中杵之，以<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="2156">白饮</a>和服。强人半钱七，<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1655">羸</a>者减之。病在<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1195">膈</a>上必吐，在<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1195">膈</a>下必利。不利，进热粥一杯；利过不止，进冷粥一杯。 身热皮粟不解，欲引衣自覆，若以水<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1560">潠</a>之、洗之，益令热却不得出，当汗而不汗则烦。假令汗出已，腹中痛，与<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="42">芍药</a>三两如上法。</dd>
    </dl>
</section>
    <section>
        <h4>方歌</h4>
        <dl>
            <dt>《长沙方歌括》陈修园</dt></p><p><dd>巴豆熬来研似脂，只须一分守成规，更加桔贝均三分，寒实结胸细辨医。</dd>
        </dl>
    </section>
','<section>
    <dl>
<dd>辨太阳病脉证并治法下</dd></p><p><dd><a class="dj-tipinfo" href="javascript:void(0)" object-extra="%7B%22book_id%22%3A3%7D" object-type="bookchapter" object-id="680">第141条</a>  病在阳，应以汗解之，反以冷水<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1560">潠</a>之；若灌之，其热被却不得去，弥更益烦，肉上粟起，意欲饮水，反不渴者，服<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="177">文蛤散</a>；若不差者，与<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="126">五苓散</a>。寒实<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1885">结胸</a>，无热证者，与三物<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="11">小陷胸汤</a>，<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="64807">白散</a>亦可服。</dd>
    </dl>
</section>
',null);
INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'三物黄芩汤（金匮要略）','null','<section>
    <h4>出处</h4>
    <p>《金匮要略》妇人产后病脉证治第二十一（《千金》附方）





</p>
</section>
<section>
    <h4>古代剂量</h4>
    <dl>
<dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="108">黄芩</a>（一两） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="77">苦参</a>（二两） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="50">干地黄</a>（四两）</dd>
    </dl>
</section>
<section>
    <h4>现代剂量</h4>
    <dl>
        <dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="108">黄芩</a>（3g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="77">苦参</a>（6g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="50">干地黄</a>（12g）</dd>
    </dl>
</section>
<section>
    <h4>煎服法</h4>
    <dl>
<dd>右三味，以水八升，煮取二升，温服一升，多吐下虫。</dd>
    </dl>
</section>
    <section>
        <h4>方歌</h4>
        <dl>
            <dt>《金匮方歌括》陈修园</dt></p><p><dd>妇人发露得风伤，头不痛兮证可详，肢苦但烦芩一两，地黄四两二参良。</dd>
        </dl>
    </section>
','<section>
    <dl>
<dd>治妇人在草蓐，自发露得风。四肢苦烦热，头痛者，与<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="153">小柴胡汤</a>，头不痛但烦者，此汤主之。</dd>
    </dl>
</section>
',null);
INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'三黄汤（金匮要略）','null','<section>
    <h4>出处</h4>
    <p>《金匮要略》卷上 中风历节病脉证并治第五（附《千金》方）



</p>
</section>
<section>
    <h4>古代剂量</h4>
    <dl>
<dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="417">麻黄</a>（五分） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="130">独活</a>（四分） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="105">细辛</a>（二分） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="109">黄芪</a>（二分） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="108">黄芩</a>（三分）</dd>
    </dl>
</section>
<section>
    <h4>现代剂量</h4>
    <dl>
        <dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="417">麻黄</a>（3.75g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="130">独活</a>（3g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="105">细辛</a>（1.5g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="109">黄芪</a>（1.5g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="108">黄芩</a>（2.25g）</dd>
    </dl>
</section>
<section>
    <h4>煎服法</h4>
    <dl>
<dd>右五味，以水六升，煮取二升。分温三服，一服小汗，二服大汗。</dd>
    </dl>
</section>
    <section>
        <h4>方歌</h4>
        <dl>
            <dt>《金匮方歌括》陈修园</dt></p><p><dd>风乘火势乱心中，节痛肢拘络不通，二分芪辛四分独，黄芩三分五麻攻。二分黄加心热端，消除腹满枳枚单。虚而气逆宜参补，牡蛎潜阳悸可安。增入蒌根能止渴，各加三分效堪观。病前先有寒邪在，附子一枚仔细看。</dd>
        </dl>
    </section>
','<section>
    <dl>
<dd>治中风手足拘急，百节疼痛，烦热心乱，恶寒，经日不欲饮食。</dd>
    </dl>
</section>
<section>
    <h4>随证化裁</h4>
    <dl>
<dd>心热，加<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="14483">大黄</a>二分；腹满，加<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="277">枳实</a>一枚；气逆，加<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="3">人参</a>三分；<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="2092">悸</a>，加<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="365">牡蛎</a>三分；渴，加<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="10">栝蒌根</a>三分；先有寒，加<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="23211">附子</a>一枚。</dd>
    </dl>
</section>
',null);
