INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'十枣汤（伤寒论）','null','<section>
    <h4>出处</h4>
    <p>《伤寒论》










</p>
</section>
<section>
    <h4>古代剂量</h4>
    <dl>
<dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="192">芫花</a>（熬） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="38">甘遂</a> <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="69">大戟</a></dd>
    </dl>
</section>
<section>
    <h4>现代剂量</h4>
    <dl>
        <dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="192">芫花</a>（熬） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="38">甘遂</a> <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="69">大戟</a></dd>
    </dl>
</section>
<section>
    <h4>煎服法</h4>
    <dl>
<dd>右三味等分，分别捣为散。以水一升半，先煮<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="230">大枣</a>肥者十枚，取八合，去滓，内药末。强人服一<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="2173">钱匕</a>，<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1655">羸</a>人服半钱，温服之，<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1571">平旦</a>服，若下少，病不除者，明日更服，加半钱，得快下利后，糜粥自养。</dd>
    </dl>
</section>
    <section>
        <h4>方歌</h4>
        <dl>
            <dt>《长沙方歌括》陈修园</dt></p><p><dd>大戟芫花甘遂平，妙将十枣煮汤行，中风表证全除尽，里气未和此法程。</dd></p><p><dt>《方剂学》邓中甲</dt></p><p><dd>十枣非君非汤剂，芫花甘遂合大戟，攻逐水饮力峻猛，悬饮水肿实证宜。</dd>
        </dl>
    </section>
','<section>
    <dl>
<dd>辨太阳病脉证并治法下</dd></p><p><dd><a class="dj-tipinfo" href="javascript:void(0)" object-extra="%7B%22book_id%22%3A3%7D" object-type="bookchapter" object-id="691">第152条</a>  太阳中风，下利，呕逆，表解者，乃可攻之；其人浆浆汗出，发作有时，头痛，心下<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1348">痞</a>硬满，引胁下痛，呕即短气，汗出不恶寒者，此表解里未和也，<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="181">十枣汤</a>主之。</dd>
    </dl>
</section>
',null);
INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'十枣汤（金匮要略）','null','<section>
    <h4>出处</h4>
    <p>《金匮要略》卷中痰饮咳嗽病脉证并治第十二




</p>
</section>
<section>
    <h4>古代剂量</h4>
    <dl>
<dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="192">芫花</a>（熬） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="38">甘遂</a> <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="69">大戟</a>（各等分）</dd>
    </dl>
</section>
<section>
    <h4>现代剂量</h4>
    <dl>
        <dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="192">芫花</a>（熬） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="38">甘遂</a> <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="69">大戟</a>（各等分）肥<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="230">大枣</a>（10枚）</dd>
    </dl>
</section>
<section>
    <h4>煎服法</h4>
    <dl>
<dd>上三味，捣筛，以水一升五合，先煮肥<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="230">大枣</a>十枚，取八合（一本作九合），去滓，内药末，强人服一<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="2173">钱匕</a>，赢人服半钱，<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1571">平旦</a>温服之。不下者，明日更加半钱，得快之后，糜粥自养。‍</dd>
    </dl>
</section>
    <section>
        <h4>方歌</h4>
        <dl>
            <dt>《长沙方歌括》陈修园</dt></p><p><dd>大戟芫花甘遂平，妙将十枣煮汤行，中风表证全除尽，里气未和此法程。</dd>
        </dl>
    </section>
','<section>
    <dl>
<dd>脉浮而细滑，伤饮。脉弦数者，有寒饮，冬夏难治。脉沉而弦者，<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1682">悬饮</a>内痛；病<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1682">悬饮</a>者，<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="181">十枣汤</a>主之。</dd>
    </dl>
</section>
',null);
INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'升降散（伤寒温疫条辨）','null','<section>
