INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'升麻鳖甲汤（金匮要略）','null','<section>
    <h4>出处</h4>
    <p>《金匮要略》卷上狐惑阴阳毒病证治第三



</p>
</section>
<section>
    <h4>古代剂量</h4>
    <dl>
<dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="14">升麻</a>（二两） <a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="16033">当归</a>（一两） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="263">蜀椒</a>（炒去汗，一两） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="1">甘草</a>（二两） <a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="7992">鳖甲</a>（手指大一片，炙） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="223">雄黄</a>（半两，研）</dd>
    </dl>
</section>
<section>
    <h4>现代剂量</h4>
    <dl>
        <dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="14">升麻</a>（6g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="16033">当归</a>（3g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="263">蜀椒</a>（炒去汗，3g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="1">甘草</a>（6g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="7992">鳖甲</a>（手指大1片，炙） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="223">雄黄</a>（1.5g，研）</dd>
