INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'四君子汤（太平惠民和剂局方）','null','<section>
    <h4>出处</h4>
    <p>《太平惠民和剂局方》卷三

</p>
</section>
<section>
    <h4>古代剂量</h4>
    <dl>
<dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="3">人参</a>（去芦） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="1">甘草</a>（炙） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="348">茯苓</a>（去皮） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="40">白术</a>（各等分）</dd>
    </dl>
</section>
<section>
    <h4>现代剂量</h4>
    <dl>
        <dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="3">人参</a>（去芦） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="1">甘草</a>（炙） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="348">茯苓</a>（去皮） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="40">白术</a>（各等分）</dd>
    </dl>
</section>
<section>
    <h4>煎服法</h4>
    <dl>
<dd>上为细末。每服二钱，水一盏，煎至七分，通口服，不拘时，入盐少许，白汤点亦得。</dd>
    </dl>
</section>
    <section>
        <h4>方歌</h4>
        <dl>
            <dt>《时方歌括》陈修园</dt></p><p><dd>苓术参甘四味同，方名君子取谦冲。增来陈夏痰涎涤，再入香砂痞满通。水谷精微阴以化，阳和布护气斯充。若删半夏六君内，钱氏书中有异功。</dd></p><p><dt>《汤头歌诀》汪昂</dt></p><p><dd>四君子汤中和义，参术茯苓甘草比。益以夏陈名六君，祛痰补气阳虚饵。除祛半夏名异功，或加香砂胃寒使。</dd></p><p><dt>《方剂学》邓中甲</dt></p><p><dd>四君子汤中和义，人参苓术甘草比，益气健脾基础剂，脾胃气虚治相宜。</dd>
        </dl>
    </section>
','<section>
    <dl>
<dd>荣卫气虚，脏腑怯弱，心腹胀满，全不思食，肠鸣泄泻，呕<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1525">哕</a>吐逆，大宜服之。常服温和脾胃，进益饮食，辟寒邪、瘴雾气。</dd>
    </dl>
</section>
',null);
