INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'桂枝麻黄各半汤（伤寒论）','null','<section>
    <h4>出处</h4>
    <p>《伤寒论》













</p>
</section>
<section>
    <h4>古代剂量</h4>
    <dl>
<dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="147">桂枝</a>（一两十六铢，去皮） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="42">芍药</a> <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="118">生姜</a>（切） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="1">甘草</a>（炙） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="417">麻黄</a>（去节）（各一两） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="230">大枣</a>（四枚，擘） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="270">杏仁</a>（二十四枚，汤浸，去皮尖及两仁者）</dd>
    </dl>
</section>
<section>
    <h4>现代剂量</h4>
    <dl>
        <dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="147">桂枝</a>（5g，去皮） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="42">芍药</a> <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="118">生姜</a>（切） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="1">甘草</a>（炙） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="417">麻黄</a>（去节）（各3g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="230">大枣</a>（1枚，擘） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="270">杏仁</a>（4g，汤浸，去皮尖及两仁者）</dd>
    </dl>
