INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'生化汤（傅青主女科）','null','<section>
    <h4>出处</h4>
    <p>《傅青主女科》产后编上卷






</p>
</section>
<section>
    <h4>古代剂量</h4>
    <dl>
<dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="16033">当归</a>（八钱） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="21">川芎</a>（三钱） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="294">桃仁</a>（十四粒，去皮尖，研） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="123">黑姜</a>（五分） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="1">炙草</a>（五分）</dd>
    </dl>
</section>
<section>
    <h4>现代剂量</h4>
    <dl>
        <dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="16033">当归</a>（24g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="21">川芎</a>（9g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="294">桃仁</a>（14粒，去皮尖，研） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="123">黑姜</a>（1.5g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="1">炙草</a>（1.5g）</dd>
    </dl>
</section>
<section>
    <h4>煎服法</h4>
    <dl>
<dd>用<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="643">黄酒</a>，<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="654">童便</a>各半，煎服。</dd>
    </dl>
</section>
    <section>
        <h4>方歌</h4>
        <dl>
            <dt>《方剂学》邓中甲</dt></p><p><dd>生化汤是产后方，归芎桃草酒炮姜，消瘀活血功偏擅，止痛温经效亦彰。</dd>
        </dl>
    </section>
','<section>
    <dl>
<dd>&nbsp;&nbsp;&nbsp;&nbsp;此症勿拘古方，妄用<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="156">苏木</a>、蓬、棱，以轻人命。其一应散血方、破血药，俱禁用。虽<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="235">山楂</a>性缓，亦能害命，不可擅用，惟<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="67601">生化汤</a>系血块圣药也。</dd></p><p><dd>&nbsp;&nbsp;&nbsp;&nbsp;又<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="63621">益母丸</a>、<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="792">鹿角</a>灰，就用<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="67601">生化汤</a>送下一钱。外用烘热衣服，暖和块痛处，虽大暑亦要和暖块痛处。有气不运而晕迷厥，切不可妄说恶血抢心，只服<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="67601">生化汤</a>为妙。俗有<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="50">生地</a>、<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="28">牛膝</a>行血；山棱、<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="101">蓬术</a>败血；<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="235">山楂</a>、沙糖消块；<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="176">蕲艾</a>、椒酒定痛，反致昏晕等症，切不可妄用。二、三、四日内，觉痛减可揉，乃虚痛也，宜<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="60597">加参生化汤</a>。</dd>
    </dl>
</section>
<section>
    <h4>随证化裁</h4>
    <dl>
<dd>&nbsp;&nbsp;&nbsp;&nbsp;如七日内，或因寒凉食物，结块痛甚者，加入<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="161">肉桂</a>八分（一作三分）於<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="67601">生化汤</a>内。如血块未消，不可加参、耆，用之则痛不止。总之，慎勿用峻利药，勿多饮姜椒<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="176">艾</a>酒。频服<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="67601">生化汤</a>，行气助血，外用热衣以暖腹。如用<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="199">红花</a>以行之，<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="156">苏木</a>、<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="28">牛膝</a>以攻之，则误。其胎气胀，用<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="98">乌药</a>、<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="90">香附</a>以顺之；<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="276">枳壳</a>、<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="164">厚朴</a>以舒之，甚有<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="273">青皮</a>、<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="277">枳实</a>、<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="311">苏子</a>以下气<a class="dj-tipinfo" href="javascript:void(0)" object-type="acupoint" object-id="154">定喘</a>；芩、连、<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="281">栀子</a>、<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="169">黄柏</a>以退热除烦。至於血结更甚，反用<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="9">承气汤</a>下之而愈结；汗多小便短涩，反用<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="126">五苓散</a>通之而愈秘，非徒无益，而又害之也。</dd></p><p><dd>&nbsp;&nbsp;&nbsp;&nbsp;凡儿生下，或停血不下，半月外尚痛，或外加肿毒，高寸许，或身热，减饮食，倦甚，必用<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="67601">生化汤</a>加<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="4">三棱</a>、<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="101">蓬术</a>、<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="161">肉桂</a>等，攻补兼治，其块自消。如虚甚，食少泄泻，只服此帖定痛，且健脾胃，进食止泻，然后服消块汤。</dd>
    </dl>
</section>
',null);
INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'生姜半夏汤（金匮要略）','null','<section>
    <h4>出处</h4>
    <p>《金匮要略》卷中呕吐哕下利病脉证治第十七




</p>
</section>
<section>
    <h4>古代剂量</h4>
    <dl>
<dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="33">半夏</a>（半升） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="118">生姜</a>汁（一升）</dd>
    </dl>
</section>
<section>
    <h4>现代剂量</h4>
    <dl>
        <dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="33">半夏</a>（12g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="118">生姜</a>汁（40ml）</dd>
    </dl>
</section>
<section>
    <h4>煎服法</h4>
    <dl>
<dd>上二味，以水三升，煮<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="33">半夏</a>取二升，内<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="118">生姜</a>汁，煮取一升半，小冷分四服，日三、夜一服，止，停后服。</dd>
    </dl>
</section>
    <section>
        <h4>方歌</h4>
        <dl>
            <dt>《金匮方歌括》陈修园</dt></p><p><dd>呕哕都非喘又非，彻由愦愦莫从违，一升姜汁半升夏，分煮同煎妙入微。</dd>
        </dl>
    </section>
','<section>
    <dl>
<dd>病人胸中似喘不端，似呕不呕，似<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1525">哕</a>不<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1525">哕</a>，彻心中<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="2247">愦愦</a>然无奈者，<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="460">生姜半夏汤</a>主之。</dd>
    </dl>
</section>
',null);
INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'生姜泻心汤（伤寒论）','null','<section>
