INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'白虎加桂枝汤（金匮要略）','null','<section>
    <h4>出处</h4>
    <p>《金匮要略》卷上疟病脉证并治第四






</p>
</section>
<section>
    <h4>古代剂量</h4>
    <dl>
<dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="76">知母</a>（六两） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="1">甘草</a>（二两，炙） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="214">石膏</a>（一斤） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="462">粳米</a>（二合） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="147">桂枝</a>（去皮，三两）</dd>
    </dl>
</section>
<section>
    <h4>现代剂量</h4>
    <dl>
        <dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="76">知母</a>（18g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="1">甘草</a>（6g，炙） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="214">石膏</a>（48g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="462">粳米</a>（6g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="147">桂枝</a>（去皮，9g）</dd>
    </dl>
</section>
<section>
    <h4>煎服法</h4>
    <dl>
<dd>上<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1489">剉</a>，每五钱，水一盏半，煎至八分，去滓，温服，汗出愈。</dd>
    </dl>
</section>
    <section>
        <h4>方歌</h4>
        <dl>
            <dt>《金匮方歌括》陈修园</dt></p><p><dd>白虎原汤论已详，桂加三两另名方，无寒但热为温疟，骨节烦疼呕又妨。</dd>
        </dl>
    </section>
','<section>
    <dl>
<dd>师曰：阴气孤绝，阳气独发，则热而少气烦冤，手足热而欲呕，名曰<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="2209">瘅疟</a>。若但热不寒者，邪气内藏于心，外舍<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="2337">分肉</a>之间，令人消铄肌肉。</dd></p><p><dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="2208">温疟</a>者，其脉如平，身无寒但热，<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="25320">骨</a>节疼烦，时呕，<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="290">白虎加桂枝汤</a>主之。</dd>
    </dl>
</section>
',null);
INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'白虎汤（伤寒论）','null','<section>
