INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'茯苓桂枝甘草大枣汤（伤寒论）','null','<section>
    <h4>出处</h4>
    <p>《伤寒论》








</p>
</section>
<section>
    <h4>古代剂量</h4>
