INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'逍遥散（太平惠民和剂局方）','null','<section>
    <h4>出处</h4>
    <p>《太平惠民和剂局方》卷九





</p>
</section>
<section>
    <h4>古代剂量</h4>
    <dl>
<dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="1">甘草</a>（微炙赤，半两） <a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="16033">当归</a>（去苗，锉，微炒） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="348">茯苓</a>（去皮，白者） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="42">芍药</a>（白） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="40">白术</a> <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="95">柴胡</a>（去苗，各一两）</dd>
    </dl>
</section>
<section>
    <h4>现代剂量</h4>
    <dl>
        <dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="1">甘草</a>（微炙赤，15g）、 <a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="16033">当归</a>（去苗，锉，微炒） 、<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="348">茯苓</a>（去皮，白者）、 <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="42">芍药</a>（白）、 <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="40">白术</a>、 <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="95">柴胡</a>（去苗)各30g。</dd>
    </dl>
</section>
<section>
    <h4>煎服法</h4>
    <dl>
<dd>上为粗末。每服二钱，水一大盏，烧<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="118">生姜</a>一块切破，<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="427">薄荷</a>少许，同煎至七分，去渣热服，不拘时候。</dd>
    </dl>
</section>
    <section>
        <h4>方歌</h4>
        <dl>
            <dt>《时方歌括》陈修园</dt></p><p><dd>逍遥散用芍当归，术草柴苓慎勿违，散郁除蒸功最捷，丹栀加入有元机。</dd></p><p><dt>《汤头歌诀》汪昂</dt></p><p><dd>逍遥散用当归芍，柴苓术草加姜薄。散郁除蒸功最奇，调经八味丹栀着。</dd></p><p><dt>《方剂学》邓中甲</dt></p><p><dd>逍遥散用当归芍，柴苓术草加姜薄，肝郁血虚脾气弱，调和肝脾功效卓。</dd>
        </dl>
    </section>
','<section>
    <dl>
<dd>血虚劳倦，五心烦热，肢体疼痛，头目昏重，心忡颊赤，口燥咽干，发热盗汗，减食嗜卧，及血热相搏，月水不调，脐腹胀痛，寒热如疟。又疗室女血弱阴虚，荣卫不和，痰嗽<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1660">潮热</a>，肌体<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1655">羸</a>瘦，渐成<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1659">骨蒸</a>。</dd>
    </dl>
</section>
',null);
INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'通脉四逆加猪胆汤（伤寒论）','null','<section>
    <h4>出处</h4>
    <p>《伤寒论》








</p>
</section>
<section>
    <h4>古代剂量</h4>
    <dl>
<dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="1">甘草</a>（二两，炙） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="123">干姜</a>（三两，强人可四两） <a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="23211">附子</a>大者（一枚，生，去皮，破八片） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="639">猪胆汁</a>（半合）</dd>
    </dl>
</section>
<section>
    <h4>现代剂量</h4>
    <dl>
        <dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="1">甘草</a>（6g，炙） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="123">干姜</a>（9g，强人可12g） <a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="23211">附子</a>（8g，生，去皮） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="639">猪胆汁</a>（2ml）</dd>
    </dl>
</section>
<section>
    <h4>煎服法</h4>
    <dl>
<dd>上四味，以水三升，煮取一升二合，去滓，内<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="639">猪胆汁</a>，分温再服，其脉即来，无猪胆，以羊胆代之。</dd>
    </dl>
</section>
    <section>
        <h4>方歌</h4>
        <dl>
            <dt>《长沙方歌括》陈修园</dt></p><p><dd>生附一枚三两姜，炙甘二两《玉函》方，脉微内竭资真汁，猪胆还加四合襄。</dd>
        </dl>
    </section>
','<section>
    <dl>
<dd>辨霍乱病脉证并治法</dd></p><p><dd><a class="dj-tipinfo" href="javascript:void(0)" object-extra="%7B%22book_id%22%3A3%7D" object-type="bookchapter" object-id="929">第390条</a> 吐已下断，汗出而厥，四肢拘急不解，脉微欲绝者，通脉<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1978">四逆</a>加<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="639">猪胆汁</a>汤主之。</dd>
    </dl>
</section>
',null);
INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'通脉四逆汤（伤寒论）','null','<section>
    <h4>出处</h4>
    <p>《伤寒论》













</p>
</section>
<section>
    <h4>古代剂量</h4>
    <dl>
<dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="1">甘草</a>（二两，炙） <a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="23211">附子</a>大者（一枚，生用，去皮，破八片） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="123">干姜</a>（三两，强人可四两）</dd>
    </dl>
</section>
<section>
    <h4>现代剂量</h4>
    <dl>
        <dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="1">甘草</a>（6g，炙） <a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="23211">附子</a>（20g，生用，去皮，破八片） <a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="123">干姜</a>（9g，强人可12g）</dd>
    </dl>
</section>
<section>
    <h4>煎服法</h4>
    <dl>
<dd>右三味，以水三升，煮取一升二合，去滓，分温再服，其脉即出者愈。</dd>
    </dl>
</section>
    <section>
        <h4>方歌</h4>
        <dl>
            <dt>《长沙方歌括》陈修园</dt></p><p><dd>一枚生附草姜三，招纳亡阳此指南，外热里寒面赤厥，脉微通脉法中探。</dd></p><p><dd>赤加葱茎用九，腹痛去葱真好手；葱去换芍二两加，呕者生姜二两偶；咽痛去芍桔须加，桔梗一两循经走；脉若不出二两参，桔梗丢开莫掣肘。（加减法）</dd>
        </dl>
    </section>
','<section>
    <dl>
<dd>辨少阴病脉证并治法</dd></p><p><dd><a class="dj-tipinfo" href="javascript:void(0)" object-extra="%7B%22book_id%22%3A3%7D" object-type="bookchapter" object-id="856">第317条</a>  少阴病，下利<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1846">清谷</a>，里寒外热，<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="2123">手足厥逆</a>，脉微欲绝，身反不恶寒，其人面色赤，或腹痛，或干呕，或咽痛，或利止，脉不出者，<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="222">通脉四逆汤</a>主之。</dd></p><p><dd>辨厥阴病脉证并治法</dd></p><p><dd><a class="dj-tipinfo" href="javascript:void(0)" object-extra="%7B%22book_id%22%3A3%7D" object-type="bookchapter" object-id="909">第370条</a>  下利<a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1846">清谷</a>，里寒外热，汗出而厥者，<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="222">通脉四逆汤</a>主之。</dd>
    </dl>
</section>
<section>
    <h4>随证化裁</h4>
    <dl>
<dd>面赤色者，加葱九茎。腹中痛者，去葱，加<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="42">芍药</a>二两。呕者，加<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="118">生姜</a>二两。咽痛者，去<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="42">芍药</a>，加<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="97">桔梗</a>一两。利止脉不出者，去<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="97">桔梗</a>，加<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="3">人参</a>二两。病皆与方相应者，乃服之。</dd>
    </dl>
</section>
',null);
