INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'雄黄熏方（金匮要略）','null','<section>
    <h4>出处</h4>
    <p>《金匮要略》卷上狐惑阴阳毒病证治第三



</p>
</section>
<section>
    <h4>古代剂量</h4>
    <dl>
<dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="223">雄黄</a></dd>
    </dl>
</section>
<section>
    <h4>现代剂量</h4>
    <dl>
        <dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="223">雄黄</a></dd>
    </dl>
</section>
<section>
    <h4>煎服法</h4>
    <dl>
<dd>上一味，为末，筒瓦二枚合之。</dd>
    </dl>
</section>
','<section>
    <dl>
<dd><a class="dj-tipinfo" href="javascript:void(0)" object-type="wiki" object-id="1637">狐惑</a>之为病，状如伤寒，默默欲眠，目不得闭，卧起不安。蚀于喉为惑，蚀于阴为狐，不欲饮食，恶闻食臭，其面目乍赤、乍黑、乍白。蚀于肛者，<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="223">雄黄</a>熏之。</dd>
    </dl>
</section>
',null);
