INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'鼻炎灵（古今名方）','null','<section>
    <h4>出处</h4>
    <p>【摘录】《古今名方》</p>
</section>
<section>
    <h4>古代剂量</h4>
    <dl>
<dd>【处方】<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="9879">苍耳子</a>（捣）60g，<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="41">白芷</a>60g，<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="193">辛夷</a>60g，<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="8650">冰片粉</a>6g，<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="427">薄荷</a>霜5g，<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="446">芝麻</a>油500ml，液状石腊1000ml。</dd>
    </dl>
</section>
<section>
    <h4>现代剂量</h4>
    <dl>
        <dd>【处方】<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="9879">苍耳子</a>（捣）60g，<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="41">白芷</a>60g，<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="193">辛夷</a>60g，<a class="dj-tipinfo" href="javascript:void(0)" object-type="prescription" object-id="8650">冰片粉</a>6g，<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="427">薄荷</a>霜5g，<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="446">芝麻</a>油500ml，液状石腊1000ml。</dd>
    </dl>
</section>
<section>
    <h4>煎服法</h4>
    <dl>
<dd>【制法】将前3味与<a class="dj-tipinfo" href="javascript:void(0)" object-type="drug" object-id="446">芝麻</a>油同放锅内，浸泡24小时，加热，待炸成黑黄色捞出，再下余3味药，搅匀，冷却后过滤，分装眼药水瓶内。</dd></p><p><dd>【用法用量】用时仰头滴鼻，每次滴1～2滴，日滴1-2次。</dd>
    </dl>
</section>
','<section>
    <dl>
<dd>【功能主治】疏风祛湿，芳香透窍，清热消肿，化瘀止痛，滋润粘膜，收缩息肉。主慢性鼻炎、萎缩性鼻炎、过敏性鼻炎、鼻息肉等。症见鼻粘膜充血，或干燥萎缩，鼻塞流涕，嗅觉失灵等。</dd>
    </dl>
</section>
',null);
INSERT INTO `cmssp_prescription`(`id`, `tag`, `name`, `book`, `summary`, `indications`, `ext_data`) VALUES (null,null,'龙胆泻肝汤（症因脉治）','null','<section>
